mapper.select:
SELECT * FROM LIFE
ORDER BY num DESC
LIMIT #{startList}, #{listSize}

SELECT * FROM LIFE
WHERE
category = #{category}
title like CONCAT('%',#{keyword},'%') OR contents like CONCAT('%',#{keyword},'%') OR introduce like CONCAT('%',#{keyword},'%')
AND (title like CONCAT('%',#{keyword},'%') OR contents like CONCAT('%',#{keyword},'%') OR introduce like CONCAT('%',#{keyword},'%'))
ORDER BY num DESC LIMIT #{startList}, #{listSize}

mapper.update:
UPDATE LIFE SET title = #{title}, introduce = #{introduce}, category = #{category},
titleImg = #{titleImg},
contents = #{contents} WHERE num = #{num}
